string commander(string ipt) {
	Signal signal;
	signal.position.end = signal.position.start;
	while (signal.finish.kuinp_ed==0) {
		if (signal.position.end >= ipt.length()-1) {
			if (ipt.c_str()[signal.position.end] == ' ' or ipt.c_str()[signal.position.end]==';') {
				signal.finish.kuinp_ed = 1;

			}
			else {
				signal.position.end++;
			}
		}
	}
	return "0";
}
void ekt() {
	string ipt;
	cout << "EktMathMode >>> ";
	cin >> ipt;
	if (ipt.c_str()[0] == '\\') /* get pos = 0 of str : ipt */ {
		commander(ipt);
	}
}

